const CovidTrackerComponent = () => {
    const [chartData, setChartData] = useState({});
    
    const chart = () => {
        let population = 0, total_cases = 0, total_deaths = 0, total_recovered;
        let cumulative_dates = [], cumulative_cases = [], cumulative_deaths = [], cumulative_recovered = [];

        CovidTrackerService.getData()
        .then(res=>{
            const latest_data = res.data.latest_data;
            population = res.data.population;
            total_deaths = latest_data.deaths;
            total_cases = latest_data.confirmed;
            total_recovered = latest_data.recovered;
            res.data.timeline.forEach(data=>{
                cumulative_dates.push(data.date);
                cumulative_cases.push(data.confirmed);
                cumulative_deaths.push(data.deaths);
                cumulative_recovered.push(data.recovered);
            })

            // Getting daily recent 7 days data
            //daily = timeline.slice(0,7);

        }).catch(err=>console.log(err));

        setChartData({
            labels: [],
            datasets: [{
                label: "Cumulative",
                data: cumulative_cases,
                backgroundColor: ["rgba(75,192,192,0.6)"],
                borderWidth: 4
            }]
        });
    }
    useEffect(()=>{chart()}, []);

    return (
        <Line data = {chartData}
            options = {{
                responsive: true,
                title: {text: "Cumulative Graph", display: true},
                scales: {
                    yAxes: [
                        {
                            ticks: {
                                autoSkip: true,
                                maxTicksLimit: 10,
                                beginAtZero: true
                            },
                            gridLines: {
                                display: false
                            }
                        }
                    ],
                    xAxes: [
                        {
                            gridLines: {
                                display: false
                            }
                        }
                    ]
                }
            }} />
    );

}


export default CovidTrackerComponent;
