import React from 'react';
import { MDBDataTableV5 } from 'mdbreact';
import { useEffect, useContext } from 'react';
import { useState } from 'react';
import axios from 'axios';
import { UserContext } from './UserContext';

export default function Basic() {

  const {user, setUser} = useContext(UserContext);
  //const [history, setHistory] = useState(null);

  useEffect(() => {
      axios.get('http://localhost:8080/consultations/patient/12')
          .then(resp => {
              const data = resp.data;
              console.log(data);
              let rows = [];
              data.patient_consultations.forEach(c=>{
                c.consultations.forEach(cons=>{
                  rows.push({
                    name: c.doctor.name,
                    cid: cons.cid,
                    date: cons.date,
                    diagnosis: cons.diagnosis,
                    medicines: cons.medicines,
                    prognosis: cons.prognosis
                  })
                })
              })
              setDatatable(prevState=>({...prevState, rows}));
          }).catch(err => {
              console.log(err);
          })
  }, []);

  const [datatable, setDatatable] = useState({
    columns: [
      {
        label: 'Doctor',
        field: 'name',
        width: 150,
        attributes: {
          'aria-controls': 'DataTable',
          'aria-label': 'Name',
        },
      },
      {
        label: 'Id',
        field: 'cid',
        width: 270,
      },
      {
        label: 'Date',
        field: 'date',
        width: 200,
      },
      {
        label: 'Diagnosis',
        field: 'diagnosis',
        sort: 'asc',
        width: 100,
      },
      {
        label: 'Medicines',
        field: 'medicines',
        sort: 'disabled',
        width: 150,
      },
      {
        label: 'Prognosis',
        field: 'prognosis',
        sort: 'disabled',
        width: 100,
      },
    ],
    rows: [
      // {
      //   name: 'Tiger Nixon',
      //   position: 'System Architect',
      //   office: 'Edinburgh',
      //   age: '61',
      //   date: '2011/04/25',
      //   salary: '$320',
      // }
    ],
  });

  return <MDBDataTableV5 hover entriesOptions={[5, 20, 25]} entries={5} pagesAmount={4} data={datatable} />;
}
