const daily_data = CovidTrackerService.getData();

export default class CovidTrackerComponent extends PureComponent {
  
  render() {
    return (
      <ResponsiveContainer width="100%" height="100%">
        <LineChart
          width={500}
          height={300}
          data={daily_data}
          margin={{
            top: 5,
            right: 30,
            left: 20,
            bottom: 5,
          }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="daily_data.date" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Line type="monotone" dataKey="daily_data.confirmed" stroke="#8884d8" activeDot={{ r: 8 }} />
          <Line type="monotone" dataKey="daily_data.deaths" stroke="#82ca9d" />
        </LineChart>
      </ResponsiveContainer>
    );
  }
}


