import { useState } from 'react';
import { Link } from 'react-router-dom';
import PatientHistory from './PatientHistory';
import axios from 'axios';

const TendToPatient = () => {
    const [patient_id, setPatientId] = useState();
    const [search, setSearch] = useState(false);
    const [consult, setConsult] = useState(false);
    const [error, setError] = useState();

    const inputHandler = (e) => {
        setPatientId(e.target.value);
        setSearch(false);
    }

    const searchHandler = () => {
        if (!isNaN(patient_id)) {
            setSearch(true);
            setError(null);
        } else {
            setSearch(false);
            setError('Please enter a valid patient id.')
        }
    }

    return (
        <div className="container d-flex flex-column align-items-center justify-content-center">
            <div className="row">
                <input type="text" placeholder="Enter patient id..." onChange={inputHandler}></input>
                <button type="button" className="btn btn-primary" onClick={searchHandler}>Search</button>
                <span className="text-center ms-2" style={{color: 'red'}} dangerouslySetInnerHTML={{__html: error}} />
            </div>
            
            {search === true ? (
                <div>
                    <PatientHistory pid={patient_id} consult={true}/>
                </div>
            )
            : <PatientHistory pid="-1"/>}
        </div>
    )
}

export default TendToPatient
