import React, {useState, useEffect} from "react";
import axios from 'axios';
import { LineChart, Label, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from "recharts";

export default function CovidTrackerComponent() {
    const [chartData, setChartData] = useState({});
    useEffect(()=>{chart()}, []);
    const chart = async () => {
      try {
        const resp = await axios.get('http://localhost:8080/covid-api/in');
        const daily_data = resp.data.timeline.reverse().filter(data=> data.recovered!==0);
        setChartData(daily_data);
      } catch (err) {
        console.log(err);
      }
    }

  return (
    <LineChart
      width={800}
      height={400}
      data={chartData}
      margin={{
        top: 5,
        right: 30,
        left: 50,
        bottom: 20
      }}
    >
      <CartesianGrid strokeDasharray="3" horizontal={true} vertical={false} />
      <XAxis dataKey="date" angle={0}/>
      <YAxis>
        <Label value="Number of cases" offset={30} angle={-90} position="left" />
      </YAxis>
      <Tooltip />
      <Legend />
      <Line type="monotone" dataKey="confirmed" dot={{ fill: '#8884d8', radius: 0.1 }}  stroke="#8884d8" />
      <Line type="monotone" dataKey="deaths" dot={{ fill: 'red' , stroke: 'red', radius: 0.2, strokeWidth: 0.3 }} />
      <Line type="monotone" dataKey="recovered" dot={{ fill: '#82ca9d'}} stroke="#82ca9d" />
    </LineChart>
  );
}
