import { useState } from 'react';
import { useForm } from 'react-hook-form';
import classNames from 'classnames';

const PatientRegistration = () => {

    //const [registration, setRegistration] = useState();

    const { register, handleSubmit, formState: { errors } } = useForm({ mode: "onChange" });

    // const inputHandler = (e) => {
    //     const name = e.target.name;
    //     const value = e.target.name;
    //     setRegistration({...registration, [name]: value});
    // }

    const submitHandler = (data) => {
        // e.preventDefault();
        // e.stopPropagation();

        // const patient_id = document.getElementsByName('patient_id').value;
        // const name = document.getElementsByName('name').value;
        // const email = document.getElementsByName('email').value;
        // const password = document.getElementsByName('password').value;
        // const confirm_password = document.getElementsByName('confirm_password').value;
        // const aadhar = document.getElementsByName('aadhar').value;
        // const city = document.getElementsByName('city').value;
        console.log(data);



    }

    return (
        <form onSubmit={handleSubmit(submitHandler)} method="post" className="needs-validation" noValidate autoComplete="off">
            <p>Please fill in this form to create an account!</p>
            <hr />
            <div className="form-group">
                <div className="row">
                    <div className="col position-relative">
                        <input type="text" className={classNames("form-control",{"is-invalid": errors.patient_id})} name="patient_id" placeholder="Patient ID" 
                        {...register('patient_id', { 
                            required: "Please enter a patient ID.",
                            pattern: {
                                value: /^\d{1,5}$/, 
                                message: "Must be 1 to 5 digits only."
                            }
                        })}/> 
                        {errors.patient_id && <div className="invalid-tooltip">{errors.patient_id.message}</div>}
                    </div>
                    <div className="col position-relative">
                        <input type="text" className={classNames("form-control",{"is-invalid": errors.name})} name="name" placeholder="Name" 
                        {...register('name', { 
                            required: "Please enter your name.",
                            minLength: {
                                value: 4,
                                message: "Minimum 4 characters"
                            },
                            // pattern: {
                            //     value: /^[a-z]([-']?[a-z]+)*( [a-z]([-']?[a-z]+)*)+$/,
                            //     message: "Invalid name."
                            // }
                        })}
                        />
                        {errors.name && <div className="invalid-tooltip">{errors.name.message}</div>}
                    </div>
                </div>
            </div>
            <div className="form-group">
                <div className="col position-relative">
                <input type="email" className={classNames("form-control",{"is-invalid": errors.email})}  name="email" placeholder="Email"
                    {...register('email', { 
                            required: "Please enter your email.",
                            pattern: {
                                value: /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/,
                                message: "Invalid email."
                            }
                        }
                    )} 
                    
                 />

                    {errors.email && <div className="invalid-tooltip">{errors.email.message}</div>}
                </div>
                
            </div>
            <div className="form-group">
                <div className="row">
                    <div className="col position-relative"><input type="password" className="form-control" name="password" placeholder="Password" 
                        
                     /></div>
                    <div className="col position-relative"><input type="password" className="form-control" name="confirm_password" placeholder="Confirm Password" 
                        
                     /></div>
                </div>
            </div>
            <div className="form-group">
                <input type="text" className="form-control" name="mobile" placeholder="Mobile" 
                    
                 />
            </div>
            <div className="form-group">
                <input type="text" className="form-control" name="aadhar" placeholder="Aadhar" 
                    
                 />
            </div>
            <div className="form-group">
                <div className="row">
                    <div className="col position-relative"><input type="text" className="form-control" name="address" placeholder="Address" 
                        
                     /></div>
                    <div className="col-4"><input type="text" className="form-control" name="city" placeholder="City" 
                        
                     /></div>
                </div>
            </div>
            <div className="form-group">
                <input type="text" className="form-control" name="confirm_password" placeholder="Date of Birth" 
                    
                 />
            </div>
            <div className="form-group">
                <label className="form-check-label"><input type="checkbox" 
                    
                 /> I hereby declare that the above information is true to the best of my knowledge.</label>
            </div>
            <div className="form-group">
                <button type="submit" className="btn btn-primary btn-lg text-dark">Sign Up</button>
            </div>
        </form>
    )
}

export default PatientRegistration
